import { checkSection } from "@/Application/Pages/MarketPlace/Assets/functions";
import ActionBtn from "../../../MP-ActionBtn/MP-ActionBtn.vue";
import MPInput from "../../../MP-Input/MP-Input.vue";
import OffersList from "../ExchangeInfoBlock/Components/OffersList/OffersList.vue";
export default {
  props: {},
  components: {
    ActionBtn,
    MPInput,
    OffersList
  },
  methods: {
    makeDeal() {
      this.$store.commit("marketPlace/openModal", [this.getSelectedItem, this.getListingType]);
    },
    onCall() {
      this.$callClient("C:MarketPlace:Listing:Call", this.getSelectedItem.id);
    },
    onText() {
      this.$callClient("C:MarketPlace:Listing:Text", this.getSelectedItem.id);
    }
  },
  computed: {
    getIsModerationSection() {
      return checkSection(this.$route, "moderation");
    },
    isShowMakeDealBtn() {
      return !checkSection(this.$route, "services");
    },
    getIsPlayerListing() {
      return this.getStatic === this.getSelectedItem.data.static;
    },
    getSelectedItem() {
      return this.$store.getters["marketPlace/getSelectedItem"];
    },
    getListingType() {
      if (checkSection(this.$route, "transportRent")) {
        return "transportRent";
      } else {
        return "listingBuy";
      }
    },
    getStatic() {
      return this.$store.getters["playerMenu/getStatic"];
    }
  }
};
