import Arrow from "../Assets/Icons/Arrow.vue";
export default {
  components: {
    Arrow
  },
  props: {
    modifications: {
      type: Array,
      required: true
    },
    activeModification: {
      type: Object,
      required: true
    }
  },
  methods: {
    setModification(type) {
      let index = this.modifications.indexOf(this.activeModification);
      let newModification = this.activeModification;
      if (type === "prev") {
        if (this.modifications[index - 1]) newModification = this.modifications[index - 1];else newModification = this.modifications[this.modifications.length - 1];
      } else {
        if (this.modifications[index + 1]) newModification = this.modifications[index + 1];else newModification = this.modifications[0];
      }
      this.$emit("setModification", newModification);
    }
  }
};
