import "core-js/modules/es.array.push.js";
import rhombusMenuData from "./Assets/rhombusMenu";
export default {
  data: function () {
    return {
      active: false,
      locked: false,
      menuData: [],
      backupData: [],
      menuSlots: 8,
      menuSlotsMax: 24,
      animation: {
        timer: [],
        timeCurrent: null,
        timeFewSlots: 1,
        timeMoreSlots: 1
      },
      playerGroup: "",
      permission: [],
      player: "Toras_Netoras",
      selected: {},
      previousCategoryList: [],
      activeCategory: "main",
      // This variable have property to dynamic change
      activeCategoryStart: "main" // Static. From what category the menu must be rendered
    };
  },
  mounted() {
    document.addEventListener("keyup", e => {
      if (this.active && !this.locked && e.keyCode >= 48 && e.keyCode <= 56) {
        document.querySelector(`.rhombus-menu__list-item[data-index="${e.key}"`).click();
      }
      if (this.active && !this.locked && e.keyCode === 27) {
        this.menuHandler(false);
      }
    });
    document.addEventListener("contextmenu", () => {
      this.toBack();
    });
    this.$onClient("C:Rhombus:Menu:Open", this.onOpen);
    this.$onClient("C:Rhombus:Menu:menuHandler", this.menuHandler);
    this.$onClient("C:Rhombus:Menu:SetPlayer", this.setPlayer);
    this.$onClient("C:Rhombus:Menu:SetPlayerGroup", this.setPlayerGroup);
    this.$onClient("C:Rhombus:Menu:SetPermission", this.setPermission);
    this.$callClient("C:Rhombus:Menu:Ready");
  },
  beforeUnmount() {
    document.removeEventListener("keyup", () => {});
    document.removeEventListener("contextmenu", () => {});
    this.$offClient("C:Rhombus:Menu:menuHandler", this.menuHandler);
    this.$offClient("C:Rhombus:Menu:Open", this.onOpen);
    this.$offClient("C:Rhombus:Menu:SetPlayer", this.setPlayer);
    this.$offClient("C:Rhombus:Menu:SetPlayerGroup", this.setPlayerGroup);
    this.$offClient("C:Rhombus:Menu:SetPermission", this.setPermission);
  },
  methods: {
    getText(text) {
      if (!text) return;
      return this.$t(text);
    },
    setDefaultCategory(category) {
      this.activeCategoryStart = category;
    },
    onOpen(defaultCategory, category, data) {
      this.setDefaultCategory(defaultCategory);
      this.renderCustom(category, data);
    },
    setPlayer(player) {
      this.player = player;
    },
    setPlayerGroup(group) {
      this.playerGroup = group;
    },
    setPermission(permission) {
      this.permission = permission;
    },
    renderCustom(category, dataCustom) {
      if (!this.locked) {
        setTimeout(() => {
          this.locked = false;
        }, document.querySelectorAll(".rhombus-menu__list-item").length * this.animation.timeCurrent + 400);
        this.activeCategory = category;
        this.backupData = dataCustom;
        this.init(this.backupData.reduce((a, b) => {
          if (b.category === this.activeCategory) {
            a.push(b);
          }
          return a;
        }, [])).then(data => {
          this.menuData = data;
          this.menuHandler(true);
          this.locked = true;
        });
      }
    },
    render(category) {
      if (!this.locked) {
        setTimeout(() => {
          this.locked = false;
        }, document.querySelectorAll(".rhombus-menu__list-item").length * this.animation.timeCurrent + 400);
        this.activeCategory = category;
        this.init(this.backupData.reduce((a, b) => {
          if (b.category === this.activeCategory) {
            a.push(b);
          }
          return a;
        }, [])).then(data => {
          this.menuData = data;
          this.menuHandler(true);
          this.locked = true;
        });
      }
    },
    async init(data) {
      let arr = [];
      let arrSubcategory = [];
      for (let i = 0; i < data.length; i++) {
        if (data[i].category === this.activeCategory) {
          if (data[i].hasOwnProperty("subCategory")) {
            for (let s = 0; s < data[i].subCategory.length; s++) {
              if (data[i].subCategory[s].hasOwnProperty("group")) {
                if (data[i].subCategory[s].group.includes(this.playerGroup) || this.permission.includes(data[i].subCategory[s].permission)) {
                  arrSubcategory.push(data[i].subCategory[s]);
                }
              } else {
                arrSubcategory.push(data[i].subCategory[s]);
              }
            }
            arr.push(...arrSubcategory);
          } else {
            if (data[i].hasOwnProperty("group")) {
              if (data[i].group.includes(this.playerGroup) || this.permission.includes(data[i].permission)) {
                arr.push(data[i]);
              }
            } else {
              arr.push(data[i]);
            }
          }
        }
      }
      if (arr.length > this.menuSlots) {
        this.animation.timeCurrent = this.animation.timeMoreSlots;
        return [].concat(arr, new Array(this.menuSlotsMax - arr.length).fill({
          empty: true,
          category: this.activeCategory
        }));
      } else {
        this.animation.timeCurrent = this.animation.timeFewSlots;
        return [].concat(arr, new Array(this.menuSlots - arr.length).fill({
          empty: true,
          category: this.activeCategory
        }));
      }
    },
    changeCategory(e) {
      if (!this.locked) {
        setTimeout(() => {
          this.locked = false;
        }, document.querySelectorAll(".rhombus-menu__list-item").length * this.animation.timeCurrent + 400);
        try {
          this.categoryResolver(e.currentTarget).then(data => {
            this.menuData = data;
            this.menuHandler(true);
          });
          this.locked = true;
        } catch (e) {
          this.stackMessage("Failed to open category. Or it is action of list", 2);
        }
      }
    },
    stackMessage(message, type) {
      this.$callClient("C:Rhombus:stackMessage", message, type);
    },
    toBack() {
      if (!this.locked && this.active && this.previousCategoryList.length) {
        let itemIndex = this.previousCategoryList.length - 1;
        let {
          id,
          category,
          trigger
        } = this.backupData.find(a => a.category === this.previousCategoryList[itemIndex]);
        this.render(this.previousCategoryList[itemIndex]);
        this.selector({
          dataset: {
            id: id,
            category: trigger,
            trigger: category
          }
        });
        this.previousCategoryList.splice(itemIndex, 1);
      }
    },
    categoryResolver(item) {
      if (item.classList.contains("empty")) return this.menuData;
      let arr = [];
      this.selector(item);
      this.previousCategoryHandler(item.dataset.category, item.dataset.trigger, item.dataset.type);
      for (let i = 0; i < this.backupData.length; i++) {
        if (item.dataset.type === "list") {
          if (this.backupData[i].category === item.dataset.trigger) {
            if (this.backupData[i].type === "list") {
              this.activeCategory = item.dataset.trigger;
              return this.init(this.backupData[i].subCategory);
            }
          }
        }
        if (item.dataset.type === "action") {
          let searchAction = this.backupData.find(a => a.category === item.dataset.category);
          if (searchAction.hasOwnProperty("subCategory")) {
            this.codeExecuter(searchAction.subCategory.find(a => a.id === Number(item.dataset.id)).action);
          } else {
            this.codeExecuter(this.backupData.find(a => a.category === item.dataset.category && a.id === Number(item.dataset.id)).action);
          }
          if (item.dataset.closeMenu) {
            return this.menuHandler(false);
          }
          this.activeCategory = item.dataset.trigger;
          for (let b = 0; b < this.backupData.length; b++) {
            if (this.backupData[b].category === item.dataset.trigger) {
              arr.push(this.backupData[b]);
            }
          }
          return this.init(arr);
        }
      }
    },
    selector(item) {
      try {
        let searchCategory = this.backupData.find(a => a.category === item.dataset.category && a.id === Number(item.dataset.id));
        console.log(this.activeCategoryStart);
        console.log(item.dataset.trigger);
        if (item.dataset.trigger === this.activeCategoryStart) {
          return this.selected = {
            title: null,
            icon: null
          };
        }
        if (!searchCategory) {
          let searchSubCategory = this.backupData.find(a => a.category === item.dataset.category);
          if (searchSubCategory.hasOwnProperty("subCategory")) {
            let searchCategoryById = searchSubCategory.subCategory.find(a => a.id === Number(item.dataset.id));
            if (searchCategoryById.hasOwnProperty("setAsSelected")) {
              if (searchCategoryById.setAsSelected) {
                this.selected = {
                  player: this.player,
                  title: searchCategoryById.title,
                  icon: searchCategoryById.icon
                };
              }
            }
            return;
          }
        }
        if (searchCategory.type === "action") {
          if (searchCategory.hasOwnProperty("closeMenu")) {
            if (searchCategory.closeMenu) return;
          }
        }
        if (searchCategory.type === "list") {
          this.selected = {
            player: this.player,
            title: searchCategory.title,
            icon: searchCategory.icon
          };
        }
      } catch (e) {
        this.stackMessage("Failed to set category", 3);
      }
    },
    previousCategoryHandler(category, trigger, type) {
      if (trigger === this.activeCategoryStart && type === "action") {
        return this.previousCategoryList = []; // For prevent stack â˜º
      }
      this.previousCategoryList.push(category);
    },
    codeExecuter(action) {
      try {
        if (typeof action === "string") {
          setTimeout(() => {
            try {
              eval(action);
              console.log(action);
            } catch (e) {
              this.stackMessage("Failed to close rhombus menu", 3);
            }
          }, document.querySelectorAll(".rhombus-menu__list-item.animate-active").length * this.animation.timeCurrent + 200);
        }
        if (typeof action === "function") {
          setTimeout(() => {
            try {
              action();
              console.log(action);
            } catch (e) {
              this.stackMessage("Failed to close rhombus menu", 3);
            }
          }, document.querySelectorAll(".rhombus-menu__list-item.animate-active").length * this.animation.timeCurrent + 200);
        }
      } catch (e) {
        this.stackMessage("Failed to execute the code", 3);
      }
    },
    menuHandler(switcher) {
      this.$nextTick(() => {
        try {
          if (!switcher) {
            if (this.locked) return;
            this.active = false;
            this.selected = {};
            this.menuData = [];
            this.previousCategoryList = [];
            return document.querySelectorAll(".rhombus-menu__list-item.animate-active").forEach((item, index) => {
              this.animation.timer.push(setTimeout(() => {
                item.classList.remove("animate-active");
              }, index * this.animation.timeCurrent));
            }), void this.$callClient("C:Rhombus:Close");
          }
          this.active = true;
          document.querySelectorAll(".rhombus-menu__list-item.animate-active").forEach(item => {
            item.classList.remove("animate-active");
          });
          for (let t = 0; t < this.animation.timer.length; t++) {
            clearTimeout(this.animation.timer[t]);
          }
          document.querySelectorAll(".rhombus-menu__list-item").forEach((item, index) => {
            this.animation.timer.push(setTimeout(() => {
              item.classList.add("animate-active");
            }, index * this.animation.timeCurrent));
          });
        } catch (ex) {
          var myStackTrace = ex.stack || ex.stacktrace || "";
          console.log(`${myStackTrace} --- ${ex}`);
        }
      });
    }
  }
};
