  computed: {
    BattlePassData() {
      return this.$store.getters["playerMenu/getBattlePassData"];
    },
    filterTabList() {
      if (this.BattlePassData.IsActive) {
        return this.tabsList;
      }
      return this.tabsList.filter(tab => tab.route !== "/playerMenu/battlePass");
    }
  },
