import { mapGetters } from "vuex";
import Cash from "../Assets/Icons/Cash.vue";
import Bank from "../Assets/Icons/Bank.vue";
export default {
  components: {
    Cash,
    Bank
  },
  props: {
    isSpecialWallet: {
      type: Boolean,
      required: true
    },
    isDonateWallet: {
      type: Boolean,
      required: true
    }
  },
  computed: {
    ...mapGetters("character", ["getMoneyString", "getSpecialWallet", "getDonateWallet"]),
    getInner() {
      if (this.isDonateWallet) {
        let donate = this.getDonateWallet;
        return `<p>${donate.value.toLocaleString("uk-UA")} </p> <coin />`;
      }
      let special = this.getSpecialWallet;
      return `${special.image(...special.interfaces.clShop)}<p>${this.getSpecialWallet.value}</p>`;
    }
  }
};
