export default {
  data() {
    return {
      warehouseData: {}
    };
  },
  computed: {
    procentOFCopasity() {
      return this.warehouseData.carrentCapacity > this.warehouseData.maxCapacity ? 100 : this.warehouseData.carrentCapacity / this.warehouseData.maxCapacity * 100;
    }
  },
  watch: {
    warehouseData() {
      setTimeout(() => {
        const innerElem = document.getElementById("WarehouseSpace__range-inner");
        innerElem.style.height = this.procentOFCopasity + "%";
      }, 100);
    }
  },
  methods: {
    closeApp() {
      this.$router.push("/");
      this.$callClient("WerehouseSpace:CloseApp");
    },
    playBeep() {
      const beepOne = document.getElementById("beep");
      beepOne.pause();
      beepOne.currentTime = 0;
      beepOne.play();
    },
    keydown(e) {
      if (e.keyCode === 27 && !window.isModalShow) {
        this.closeApp();
      }
    },
    setData(data) {
      this.warehouseData = data.warehouseData;
    },
    bindClose() {
      window.addEventListener("keyup", this.keydown);
    }
  },
  beforeUnmount() {
    window.removeEventListener("keyup", this.keydown);
    this.$offClient("W:WerehouseSpace:setData", this.setData);
  },
  mounted() {
    this.bindClose();
    this.$onClient("W:WerehouseSpace:setData", this.setData);
    this.$callClient("C:WerehouseSpace:Ready");
  }
};
