import MPTimer from "../../MarketPlace/Components/MP-Timer/MP-Timer.vue";
import MPTip from "../../MarketPlace/Components/MP-Tip/MP-Tip.vue";
import MPGender from "../../MarketPlace/Components/MP-Gender/MP-Gender.vue";
import { getIsImgFull, getItemCardImgPath, formatNumber } from "../../MarketPlace/Assets/functions";
import CdnResolverImage from "@/Components/CdnResolverImage.vue";
export default {
  props: {
    itemData: {
      type: Object,
      required: true
    }
  },
  components: {
    MPTimer,
    MPTip,
    MPGender,
    CdnResolverImage
  },
  methods: {
    getItemCardImgPath() {
      return getItemCardImgPath(this.itemData);
    },
    isCardImgFull(type) {
      return getIsImgFull(type);
    },
    onUnload() {
      if (this.itemData?.count) {
        this.$store.commit("marketPlace/openModal", [this.itemData, "stockUnload"]);
      } else {
        this.$callClient("C:MarketPlace:Stock:Unload", this.itemData.id, 1);
      }
    },
    formatNumber(num) {
      return formatNumber(num);
    }
  },
  computed: {
    isShowQuantityTip() {
      return this.itemData?.count !== undefined;
    },
    isShowGender() {
      return this.itemData?.gender !== undefined;
    },
    isShowTimer() {
      return this.itemData?.endTime;
    }
  }
};
