import Input from "../Components/Search/Input.vue";
import Star from "../Assets/Icons/Star.vue";
import SearchProperties from "../Components/Search/SearchProperies.js";
import Lock from "../Assets/Icons/Lock.vue";
export default {
  components: {
    Input,
    Star,
    Lock
  },
  data() {
    return {
      result: {}
    };
  },
  props: {
    type: {
      type: String,
      required: true
    }
  },
  computed: {
    getInputParams() {
      return this.type === "human" ? {
        placeholder: `Введите имя и фамилию человека (Ivan_Ivanov)`,
        RegExp: "^[a-zA-Z_]+$"
      } : {
        placeholder: `Введите номер транспортного средства (AB1234CD)`,
        RegExp: "^[a-zA-Z0-9]+$"
      };
    },
    showStars() {
      return this.result.stars !== undefined;
    },
    classifiedText() {
      return this.result.classified ? `Рассекретить` : `Засекретить`;
    },
    showResult() {
      return !this.result.classified && this.result.classified !== undefined || this.result.show || this.type === "vehicle";
    }
  },
  methods: {
    clearData() {
      this.result = {};
    },
    searchItem(value) {
      let trigger = this.type === "human" ? "Tablet:SearchHuman" : "Tablet:SearchVehicle";
      if (value !== "") {
        this.$RPC.call(trigger, value).then(([result]) => {
          this.result = result;
        }).catch(() => {});
      }
    },
    starActive(i) {
      return i <= this.result.stars;
    },
    getProperty(property) {
      return SearchProperties[property];
    },
    getValue(value) {
      return Array.isArray(value) ? value.join("<br>") : value;
    },
    setClassify() {
      this.$RPC.call("Tablet:SetClassifyHuman", this.result.name).then(([result]) => {
        this.result.classified = result;
      });
    },
    setStars() {
      this.$RPC.call("Tablet:OpenStars").then(() => this.$EBus.emit("Tablet.Search->PoliceFBI:OpenStarsModal", this.result.name, this.result.stars)).catch(() => {});
    }
  }
};
