import BankInput from "../Components/BankInput.vue";
export default {
  components: {
    BankInput
  },
  data() {
    return {
      account: "",
      amount: ""
    };
  },
  methods: {
    onInput(e) {
      e.stopPropagation();
      if (!/^[0-9]+$/.test(e.target.value)) e.target.value = e.target.value.substring(0, e.target.value.length - 1);
    },
    onAccountInput(e) {
      this.onInput(e);
      if (e.target.value.length > 6) e.target.value = e.target.value.substring(0, e.target.value.length - 1);
      this.account = e.target.value;
    },
    onAmountInput(e) {
      this.onInput(e);
      if (Number(e.target.value) > 1e9) e.target.value = 1e9 - 1;
      this.amount = e.target.value;
    },
    onAction() {
      this.$callClient("C:Bank:Conversion", this.account, this.amount);
    }
  },
  created() {
    this.$callClient("C:Bank:ConversionReady");
  },
  beforeUnmount() {
    this.$callClient("C:Bank:ConversionDestroyed");
  }
};
