import Put from "../Icons/Put.vue";
import Get from "../Icons/Get.vue";
const Images = {
  put: Put,
  get: Get
};
export default {
  components: {
    Put,
    Get
  },
  props: {
    item: {
      type: Object,
      required: true
    }
  },
  computed: {
    getClass() {
      return this.item.ammount > 0 ? "put" : "get";
    },
    getAmount() {
      return `$ ${this.item.ammount > 0 ? "+" : "-"} ${this.$n(this.item.ammount)}`;
    },
    getIcon() {
      return Images[this.item.ammount > 0 ? "put" : "get"];
    },
    getDate() {
      return `${this.item.time} | ${this.item.date.replaceAll("/", ".")}`;
    },
    getTitle() {
      return this.item.otype == "account" ? this.item.ammount > 0 ? "Внесение наличных" : "Снятие наличных" : this.item.ammount > 0 ? "Входящий перевод" : "Исходящий перевод";
    }
  }
};
