import Put from "./Icons/Put.vue";
import Get from "./Icons/Get.vue";
import House from "./Icons/House.vue";
import Business from "./Icons/Business.vue";
import Office from "./Icons/Office.vue";
import Mansion from "./Icons/Mansion.vue";
const Icons = {
  put: Put,
  get: Get,
  house: House,
  business: Business,
  mansion: Mansion,
  office: Office
};
export default {
  props: {
    tabs: {
      type: Array,
      required: true
    },
    active: {
      type: Object,
      required: true
    }
  },
  computed: {
    filterTabs() {
      return this.tabs.filter(x => this.isAvailable(x.route));
    }
  },
  methods: {
    isAvailable(type) {
      if (this.$store.getters.getBankTaxes[type]) return this.$store.getters.getBankTaxes[type].tax != 0;else return true;
    },
    isTabActive(tab) {
      return this.active == tab;
    },
    setActiveTab(tab) {
      if (!this.isTabActive(tab)) this.$emit("change", tab);
    },
    getIcon(tab) {
      return Icons[tab.image];
    }
  }
};
