export default {
  data() {
    return {
      time: 0,
      isNoShowAgain: false
    };
  },
  methods: {
    closeApp() {
      this.$callClient("C:Draw.Close", this.isNoShowAgain);
    },
    setData(data) {
      this.time = data;
    },
    onKeyUp(e) {
      if (e.key === "Escape") {
        this.closeApp();
      }
    }
  },
  mounted() {
    this.$onClient("C:Draw:setData", this.setData);
    this.$callClient("C:Draw:Ready");
    window.addEventListener("keyup", this.onKeyUp);
  },
  beforeUnmount() {
    this.$offClient("C:Draw:setData", this.setData);
    window.removeEventListener("keyup", this.onKeyUp);
  }
};
