export default {
  data() {
    return {
      navigationTabs: [{
        route: "/bank/management/my",
        title: "Управление счетом",
        img: "Coin"
      }, {
        route: "/bank/conversion",
        title: "Перевести на счет",
        img: "Conversion"
      }, {
        route: "/bank/taxes",
        title: "Налоги",
        img: "Taxes"
      }, {
        route: "/bank/management/family",
        title: "Счет семьи",
        img: "Characters"
      }, {
        route: "/bank/management/fraction",
        title: "Счет организации",
        img: "Characters"
      }],
      hints: [{
        keys: ["↑", "↓"],
        title: "Переключение вкладок"
      }, {
        keys: ["Esc"],
        title: "Закрыть окно"
      }]
    };
  },
  computed: {
    getVuexGettersGetBankData() {
      return this.$store.getters["playerMenu/getBankData"];
    }
  },
  methods: {
    onChangeRoute(multiplier) {
      let activeRoute = this.navigationTabs.findIndex(tab => this.$route.fullPath.includes(tab.route));
      let nextRouteIndex = this.navigationTabs[activeRoute + multiplier] ? activeRoute + multiplier : multiplier == 1 ? 0 : this.navigationTabs.length - 1;
      let nextRoute = this.navigationTabs[nextRouteIndex];
      this.$router.push(nextRoute.route);
    },
    onKeyDown(e) {
      if (e.keyCode == 38 || e.keyCode == 40) {
        let multiplier = e.keyCode == 40 ? 1 : -1;
        this.onChangeRoute(multiplier);
      }
    },
    onKeyUp(e) {
      if (e.keyCode === 27 && !window.isModalShow) this.closeBank();
    },
    closeBank() {
      this.$callClient("C:Bank:CloseMenu");
    }
  },
  created() {
    window.addEventListener("keydown", this.onKeyDown);
    window.addEventListener("keyup", this.onKeyUp);
  },
  beforeUnmount() {
    window.removeEventListener("keydown", this.onKeyDown);
    window.addEventListener("keyup", this.onKeyUp);
  }
};
