export default {
  props: {
    placeholder: {
      type: String,
      required: true
    },
    value: {
      type: [String, Number],
      required: true
    }
  },
  methods: {
    setFocus() {
      if (this.$refs.input) this.$refs.input.focus();
    },
    onFocus(e) {
      e.stopPropagation();
      this.$emit("focus", e);
    },
    onBlur(e) {
      e.stopPropagation();
      this.$emit("blur", e);
    },
    onChange(e) {
      e.stopPropagation();
      this.$emit("change", e);
    },
    onInput(e) {
      e.stopPropagation();
      this.$emit("input", e);
    }
  }
};
