import nearIcon from "../../../Components/nearIcon.vue";
import openIcon from "../../../Components/openIcon.vue";
import checkMark from "../../../Components/checkMark.vue";
import trackList from "../../../Assets/trackList.json";
export default {
  data() {
    return {
      selectedTypeBattle: 1,
      selectedSoundId: 1,
      selectedPlayerId: -1,
      availableSongsId: [1, 2],
      soundList: trackList
    };
  },
  watch: {
    selectedTypeBattle() {
      this.selectedPlayerId = -1;
    }
  },
  props: {
    playersNear: Array,
    selectDanceData: Object
  },
  methods: {
    getSoundItemById(id) {
      return this.soundList.find(sound => sound.id == id).name;
    },
    getPlayerNameById(id) {
      return this.playersNear.find(player => player.id === id)?.name || "anyone";
    },
    onStartGame() {
      this.$callClient("W:DanceMiniGame:StartGame", {
        soundId: this.selectedSoundId,
        player: this.selectedPlayerId
      });
    }
  },
  components: {
    nearIcon,
    openIcon,
    checkMark
  }
};
