import CustomRange from "../../../Components/CustomRange/CustomRange.vue";
import CdnResolverImage from "../../../Components/CdnResolverImage.vue";
import Wave from "./Components/Wave.vue";
export default {
  data() {
    return {
      stantionId: null,
      selectedGas: null,
      min: 0,
      max: 0,
      selectedCountOfGas: 15,
      gasList: null,
      productList: null,
      isActive: false
    };
  },
  methods: {
    handleScroll(event) {
      const container = event.currentTarget;
      if (event.deltaY > 0) {
        container.scrollLeft += 50;
      } else {
        container.scrollLeft -= 50;
      }
    },
    GetImagePath(item) {
      return `cdn/inventory/other/${item.id}.png`;
    },
    setInnerValue(value) {
      this.selectedCountOfGas = value;
    },
    setFull() {
      this.selectedCountOfGas = this.max;
    },
    buyGas(type) {
      const buy = {
        paymentType: type,
        selectedGas: this.selectedGas.id,
        value: this.selectedCountOfGas
      };
      this.$callClient("C:GasStantion:BuyGas", buy);
    },
    selectGas(gasItem) {
      if (!this.max) {
        return;
      }
      this.selectedGas = gasItem;
    },
    buyProduct(type, productId) {
      const buy = {
        paymentType: type,
        productId: productId
      };
      this.$callClient("C:GasStantion:BuyProduct", buy);
    },
    setData(data) {
      Object.assign(this.$data, data);
      console.log(this.min, this.max);
      this.selectedCountOfGas = Math.floor((data.max + data.min) / 2);
      this.isActive = true;
    },
    getSelectedGasItemNameById() {
      return " " + this.gasList.find(item => item.id == this.selectedGas).name;
    },
    closeApp() {
      this.isActive = false;
      this.$callClient("C:GasStantion:Close");
    },
    keyup(e) {
      if (e.keyCode === 27 && !window.isModalShow) {
        this.closeApp();
      }
    }
  },
  components: {
    CdnResolverImage,
    CustomRange,
    Wave
  },
  mounted() {
