export default {
  data() {
    return {
      zoom: 1,
      organizationName: "Dronto GANG",
      organizationsList: ['Dronto GANG', 'Night Will Gang', 'Trap EMS'],
      tabs: [{
        path: "members/main",
        title: "Список членов \n организации",
        image: "Members"
      }, {
        path: "ranks/main",
        title: "Управление \n рангами",
        image: "Ranks"
      }, {
        path: "transport/main",
        title: "Управление \n транспортом",
        image: "Transport"
      }, {
        path: "actions/main",
        title: "Действия",
        image: "Actions"
      }],
      hints: [{
        keys: ["Esc"],
        title: "Закрыть планшет"
      }]
    };
  },
  methods: {
    onResize() {
      let xZoom = window.innerWidth / 1600;
      let yZoom = window.innerHeight / 900;
      this.zoom = Number((xZoom < yZoom ? xZoom : yZoom).toFixed(3));
    },
    onExit(e) {
      if (e.keyCode == 27) {
        this.$callClient("Controls:Close");
      }
    },
    setData(data) {
      this.organizationName = data.organizationName;
    }
  },
  created() {
    this.$onClient("Controls:SetData", this.setData);
    this.$callClient("Controls:Ready");
    window.addEventListener("resize", this.onResize);
    window.addEventListener("keyup", this.onExit);
    this.onResize();
  },
  beforeUnmount() {
    window.removeEventListener("resize", this.onResize);
    window.removeEventListener("keyup", this.onExit);
  }
};
