import MPInput from "../../MP-Input/MP-Input.vue";
import MPActionBtn from "../../MP-ActionBtn/MP-ActionBtn.vue";
import InfoBlock from "./InfoBlock.vue";
export default {
  props: {
    modalData: {
      type: Object,
      required: true
    },
    modalType: {
      type: String,
      required: true
    }
  },
  data() {
    return {
      postedQuantityInput: null,
      priceOfCount: null
    };
  },
  computed: {
    getModalData() {
      return [[{
        title: "Назва товару",
        value: this.modalData.name
      }], [{
        title: "Доступна кількість",
        value: `${this.modalData.count} ${this.$t("шт.")}`
      }]];
    },
    getSelectedItem() {
      return this.$store.getters["marketPlace/getSelectedItem"];
    },
    getIsCanUpload() {
      return this.postedQuantityInput && this.priceOfCount;
    },
    getOfferMaxPostingPrice() {
      return this.$store.getters["marketPlace/getOfferMaxPostingPrice"];
    }
  },
  methods: {
    setPostedQuantity(value) {
      this.postedQuantityInput = value;
    },
    setPriceOfCount(value) {
      this.priceOfCount = value;
    },
    add() {
      if (this.postedQuantityInput && this.priceOfCount) {
        if (this.modalType === "exchangeAdd") {
          this.$callClient("C:MarketPlace:Exchange:Add", this.getSelectedItem.id, this.priceOfCount, this.modalData.id, this.postedQuantityInput);
        } else if (this.modalType === "exchangeItemAdd") {
          this.$callClient("C:MarketPlace:ItemCard:Add:Exchange", this.modalData.id, this.priceOfCount, this.postedQuantityInput);
        }
      }
    }
  },
  components: {
    MPActionBtn,
    MPInput,
    InfoBlock
  }
};
